(function(step, stepResult, jasmine, describe) {
	
	describe('FCM Creation Test', function() {
		beforeEach(function() {
			var gr = new GlideRecord('x_150261_fcm_finance_case');
			gr.initialize();
			gr.setValue('opened_by', 'd8f57f140b20220050192f15d6673a98');  //ATF.User
			gr.setValue('contact_type', 'phone');
			gr.setValue('category', 'ar');
			gr.setValue('short_description', 'ATF Test (Jasmine)');
			gr.setValue('short_description', 'ATF Test (Jasmine)\nATF Test (Jasmine)\nATF Test (Jasmine)\nATF Test (Jasmine)\nATF Test (Jasmine)');
			gr.insert();
		});

		it("Expect finance case exists", function() {
			var gr = new GlideRecord('x_150261_fcm_finance_case');
			gr.addQuery('opened_by', 'd8f57f140b20220050192f15d6673a98');  //ATF.User
			gr.query();
			expect(gr.getRowCount()).toEqual(1);
		});

		afterEach(function() {
			var gr = new GlideRecord('x_150261_fcm_finance_case');
			gr.addQuery('opened_by', 'd8f57f140b20220050192f15d6673a98');  //ATF.User
			gr.deleteMultiple();
		});
	});
	
})(step, stepResult, jasmine, describe);

jasmine.getEnv().execute();
