sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

	//var link = new global.AssessmentUtils().getAssessmentInstanceURL(current.sys_id);
	var link = 'https://' + gs.getProperty('instance_name')+  '.service-now.com/sp?id=take_survey&instance_id=' + current.sys_id;
	var url =  '<a href="' + link + '">' + current.number + '</a>';
	template.print(url);

})(current, template, email, email_action, event);
