var FCMServerSideUtils = Class.create();
FCMServerSideUtils.prototype = {
    initialize: function() {
    },

	// ServiceNow Demo #1
	// Added by Marcello Correa on July 9, 2018

	updateFCMRecords: function() {

		var caseGR = new GlideRecord('x_150261_fcm_finance_case');
		caseGR.addActiveQuery();
		caseGR.query();
		while (caseGR.next()) {
			caseGR.short_description = gs.getProperty('x_150261_fcm.fcm.script.short_description');

			// Option #1
			caseGR.priority = 3;
			//caseGR.priority = gs.getProperty('x_150261_fcm.fcm.script.short_description');

			// Option #2
			//caseGR.setValue('priority', 3);
			//caseGR.setValue('priority', gs.getProperty('x_150261_fcm.fcm.script.short_description'));

			// Option #3 - it will only in global scope not scoped
			//caseGR.setDisplayValue('priority', '3  Moderate');

			caseGR.update();
		}

	},

    type: 'FCMServerSideUtils()'
};
